    /// <summary>
    /// Direction of an element (horizontal or vertical)
    /// </summary>
    public enum Orientation
    {

        /// <summary>
        /// Left to right 
        /// </summary>
        Horizontal,

        /// <summary>
        /// Bottom to top
        /// </summary>
        Vertical
    }
