        /// <summary>
        /// Make the attribute for the foreground and background colors.
        /// </summary>
        /// <param name="fore">Foreground.</param>
        /// <param name="back">Background.</param>
        /// <returns></returns>
        public abstract Attribute MakeAttribute(Color fore, Color back);

        /// <summary>
