        // See: https://github.com/gui-cs/Terminal.Gui/issues/357

        [StructLayout(LayoutKind.Sequential)]
        public struct CONSOLE_SCREEN_BUFFER_INFOEX
        {
            public uint cbSize;
            public Coord dwSize;
            public Coord dwCursorPosition;
